package arrays;

public class Arrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Write a program to sum all the values of a given Array in Java.//
		int[] array = {1,2,3,4};
		int sum = 0;
		for (int item : array) {
			sum += item;
		} 
		System.out.println(sum);
		
		
		
		

	}

}
